document.addEventListener("DOMContentLoaded",(function(){function t(t){return t.toString().replace(/\B(?=(\d{3})+(?!\d))/g," ")}const e=new IntersectionObserver((function(e,n){e.forEach((e=>{e.isIntersecting&&(!function(e){const n=parseInt(e.textContent.replace(/\s/g,""),10);let o=null;requestAnimationFrame((function r(c){o||(o=c);const u=c-o,a=Math.min(u/1250,1),i=Math.floor(a*n);e.textContent=t(i),a<1?requestAnimationFrame(r):e.textContent=t(n)}))}(e.target),n.unobserve(e.target))}))}),{root:null,threshold:.5});document.querySelectorAll(".number-counter").forEach((t=>{e.observe(t)}))}));