const lazyVideos=document.querySelectorAll("video.video-lazy-load");if("IntersectionObserver"in window){const e=new IntersectionObserver(((r,t)=>{r.forEach((r=>{if(r.isIntersecting){const t=r.target;t.querySelectorAll("source[data-src]").forEach((e=>{e.src=e.getAttribute("data-src"),e.removeAttribute("data-src")})),t.load(),e.unobserve(t)}}))}));lazyVideos.forEach((r=>{e.observe(r)}))}